mapper.select:
select `id`,`alert_threepart_id`,`create_by`,`operation`,`create_time` from zc_alert_threepart_record

     and id = #{alertThreepartRecordId} 
     and alert_threepart_id = #{alertThreepartId} 
     and create_by = #{openid} 
     and operation = #{operation} 
     and operation != #{notOperation} 
     and create_time = #{operationTime} 



        order by ${sort} ${order}


        order by create_time desc



    limit #{offset}, #{limit}
